window.addEventListener("scroll",()=>{let e=window.scrollY,t=document.querySelector(".categories");if(e>=75){if(t.classList.contains("categories--fixed"))return;t.classList.add("categories--fixed")}else{if(!t.classList.contains("categories--fixed"))return;t.classList.remove("categories--fixed")}});const searchFormInput=document.querySelector(".search-form > .input");searchFormInput.addEventListener("focus",e=>{e.target.classList.add("focus"),e.target.nextElementSibling.classList.add("focus"),e.target.parentElement.classList.add("search-form--shadow")}),searchFormInput.addEventListener("blur",e=>{e.target.classList.remove("focus"),e.target.nextElementSibling.classList.remove("focus"),e.target.parentElement.classList.remove("search-form--shadow")});const activeCategory=document.querySelector(".categories__active-element"),categories=document.querySelectorAll(".categories__item");function selectCategory(e){let t=e.target,s=t.clientWidth+20;activeCategory.style.width=s+"px",activeCategory.style.left=t.offsetLeft-10+"px"}categories.forEach(e=>e.addEventListener("mouseover",selectCategory));